package com.wipro;

public class Arrays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int size=4;
		int[] students=new int[size];
		System.out.println(students.length);//prints the length of array
		students[0]=101;
		students[1]=102;
		students[2]=103;
		students[3]=104;
		System.out.println("The array element prent at index 1 is: "+students[1]);//prints the value that stored at index 1
		
	}

}
